
public class P4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Write a program to print the following pattern
		 int num =1;
		for(int i=1; i<=3; i++) {
			for(int j=1; j<=3; j++) {
				
				System.out.print(num*num+" ");
				
				num++;
				
			}
			System.out.println();
		}

	}

}
